                                                          END) AS OCT, SUM(CASE SUBSTRING(MONTHNAME, 1, 3)
                                                                             WHEN 'NOV' THEN SDL
                                                                             ELSE 0
                                                                           END) AS NOV,
                    SUM(CASE SUBSTRING(MONTHNAME, 1, 3)
                          WHEN 'DEC' THEN SDL
                          ELSE 0
                        END) AS DEC
            FROM    PAYROLL a
            WHERE   sdl IS NOT NULL
            GROUP BY company_name
