                                            prepare_payroll_detail d
                                  WHERE     h.trx_id = d.trx_id AND
                                            emp_id = #temp.emp_code AND
                                            MONTH(start_period) = @month AND
                                            YEAR(start_period) = @year)       
       SELECT   *
       FROM     #temp
