       @year INT
AS 
       BEGIN  
             SELECT isnull(a.emp_name,'')+' '+isnull(a.emp_lname,'') emp_name, b.trx_amount, b.trx_type, c.[desc]
             FROM   employee a ,
                    emp_additions b ,
                    additions_types c
