CREATE PROC [dbo].[sp_getLockAddition]
       @month1 INT ,
       @month2 INT ,
       @year1 INT ,
       @year2 INT ,
       @emp_code INT ,
       @compid INT
AS 
       IF @emp_code = -1 
          BEGIN   

                SELECT  ISNULL(COUNT(ead.trx_id), 0)
                FROM    (SELECT EA.*
                         FROM   emp_additions EA
                         INNER JOIN Employee Em ON EA.Emp_Code = Em.Emp_Code
                         WHERE  Em.Company_ID = @compid
                        ) ead
                WHERE   ((MONTH(ead.trx_period) = @month1 AND
