       ELSE 
          BEGIN   
                SELECT  ISNULL(COUNT(ead.trx_id), 0)
                FROM    (SELECT EA.*
                         FROM   emp_additions EA
                         INNER JOIN Employee Em ON EA.Emp_Code = Em.Emp_Code
                         WHERE  Em.Company_ID = @compid
                        ) ead
                WHERE   ead.emp_code = @emp_code AND
                        ((MONTH(ead.trx_period) = @month1 AND
