CREATE PROCEDURE [dbo].[sp_CheckSuperAdminCount]
       (
        @CompanyID INT ,
        @EmployeeID INT = ''  
       )
AS 
       BEGIN    
  
             DECLARE @grpname AS VARCHAR(20)  
    
             SELECT @grpname = UG.GroupName
             FROM   Employee E
             JOIN   UserGroups UG ON E.GroupID = UG.GroupID
             WHERE  E.Emp_Code = @EmployeeID  
  
             IF @grpname = 'Super Admin' 
                BEGIN  
                      SELECT    COUNT(*)
