                                                                               ([DayNameActa] = 'Saturday')
                                                                          THEN (Emp_Worked_Time_in_Mins -
                                                                                (Set_WorkingTime_in_Mins / 2))
                                                                          WHEN (Emp_Worked_Time_in_Mins > Set_WorkingTime_in_Mins) AND
                                                                               WorkingPattern = 5.5 AND
                                                                               ([DayNameActa] = 'Sunday')
                                                                          THEN (Emp_Worked_Time_in_Mins)
                                                                          WHEN (Emp_Worked_Time_in_Mins > Set_WorkingTime_in_Mins) AND
