            --                      SELECT    @wheresessid = 'Where SessionID Not In (' + CONVERT(VARCHAR, @sessid) + ')'                                        
            --                      SELECT    @select = 'Delete From #ActLogsTemp ' + @wheresessid                                        
            --                      EXEC (@select)                                    
            END
        ELSE
            BEGIN
                SET @softdelete=0
            END

        SELECT ROWID = IDENTITY(INT, 1, 1),'NH' = CASE
                                                    WHEN TodayPH = 1 THEN 0
