                                                                               AND AL.TerminalSN = R.Sub_Project_ID
                                                                               AND AL.UserID = R.Time_Card_No
                                                                               AND (CONVERT(DATETIME, AL.TimeEntry, 103) BETWEEN CONVERT(DATETIME, R.TimeStart, 103) AND CONVERT(DATETIME, R.TimeEnd, 103))
