                                                                            ORDER  BY CONVERT(DATETIME, AL.TimeEntry, 103) DESC)
																			WHEN RosterType = 'FLEXIBLE' THEN (SELECT TOP 1 CONVERT(DATETIME, AL.TimeEntry, 103)
