CREATE PROC [dbo].[sp_GetRosterLockedDate] (@RosterCurID INT)
AS --Select * From   
--(  
-- Select (Roster_Name+ ' '+Roster_Date) RosterName,Roster_ID,Roster_Date,(CurRowIn+CurRowOUt+OTRRowIn+OTRRowOut) ValidRow From  
-- (  
