        @Remarks VARCHAR(50) ,
        @EmpID VARCHAR(8000) ,
        @Action INT ,
        @retval INT OUTPUT        
       )
AS 
       DECLARE @select VARCHAR(8000)        
       DECLARE @where VARCHAR(8000)         
      
       SET @where = ''
       SET @select = ''
  
       BEGIN    
        
             IF LEN(@EmpID) > 0 
                BEGIN                                                                                      
                      IF (@Action = 0) 
                         BEGIN  
                               SELECT   @where = 'WHERE Emp_Code IN (' + @EmpID + ')'                                                                                
                         END  
                      IF (@Action = 1) 
                         BEGIN  
