--                     ELSE REPLACE(CONVERT(NVARCHAR(10), TERMINATION_DATE, 120), '-', '') END AS [CessationDate],    

   CASE WHEN CAST(YEAR(TERMINATION_DATE) AS INT)  = CAST(@year AS int) THEN CONVERT(NVARCHAR(10),TERMINATION_DATE,112)      
