AS  
    BEGIN  
        DECLARE @colname VARCHAR(8000)  
        DECLARE @select VARCHAR(8000)  
        DECLARE @whereproj VARCHAR(8000)  
        DECLARE @whereemp VARCHAR(8000)  
        DECLARE @wheresessid VARCHAR(100)  
        DECLARE @startdate AS DATETIME  
