AS -- sp_fund_monthly_report_summary 17,2010
                     
                    
       CREATE TABLE #tempfundtype
              (
               fundtype VARCHAR(20) COLLATE DATABASE_DEFAULT
              )                      
       INSERT   INTO #tempfundtype
