                LEFT OUTER JOIN leaves_allowed l ON l.leave_type = e.leave_type AND
                                                    l.leave_type = (SELECT id FROM leave_types
                                                                            WHERE type= 'Annual Leave'
                                                                   ) AND
                                                    leave_year = @year AND
                                                    l.group_id = @Groupid
                GROUP BY e.emp_id, l.leaves_allowed

       SELECT   *
       FROM     #temp1
