                                ISNULL(empCPF, 0) empCPF, ISNULL(employerCPF, 0) employerCPF, fund_type,
                                ISNULL(fund_amount, 0) fund_amount, CASE WHEN fund_type = 'SINDA' THEN FUND_AMOUNT
                                                                         ELSE 0
