                                                                         ELSE 0
                                                                    END sinda_fund,
                                CASE WHEN fund_type = 'MBMF' THEN FUND_AMOUNT
                                     ELSE 0
                                END mbmf_fund, CASE WHEN fund_type = 'CDAC' THEN FUND_AMOUNT
                                                    ELSE 0
                                               END cdac_fund, CASE WHEN fund_type = 'ECF' THEN FUND_AMOUNT
                                                                   ELSE 0
                                                              END ecf_fund,                   
