                                                                                                                                                                                                                                                                                                                                                                                         ELSE ''
                                                                                                                                                                                                                                                                                                                                                                                       END,CONVERT(VARCHAR(10), CONVERT(DATETIME, trx_period, 105), 103) trx_period_copy
                FROM   [emp_additions] e,additions_types a,employee b
                WHERE  e.[emp_code] = @empcode
                   AND e.trx_type = a.id
                   AND e.emp_code = b.emp_code
                   AND (e.claimstatus = 'Approved'
