                    b.trx_type = c.id AND
                    a.emp_code = @emp_code AND
                    DATEPART(yy, trx_period) = @year AND
                    DATEPART(mm, trx_period) = @month AND
                    (b.claimstatus IS NULL OR
                     b.claimstatus = 'Approved'
                    )  
