                                                              FROM      public_holidays   WHere  (CompanyID=@compid Or CompanyID=-1) And
--where month(holiday_date) = @month AND year(holiday_date) = @year AND companyid IN (-1,@compid)))  
																		MONTH(holiday_date) = @month AND
                                                                        YEAR(holiday_date) = @year AND
