       SELECT   userID, empName, empCategory, remarks, tapDate, timeentry, dept,
                CONVERT(VARCHAR(20), INTIME, 108) 'INTIME', CONVERT(VARCHAR(20), OUTTIME, 108) 'OUTTIME', OT1, OT2, Flag
