--SP_empleave_Add 2, '01/11/2010', '16/11/2010', 8, '', 'Open', 10, 2, 0, '', '', 2010  
CREATE PROCEDURE [dbo].[Sp_empleave_add](    
               @emp_code      [VARCHAR](50),    
               @start_date    VARCHAR(10),    
               @end_date      VARCHAR(10),    
               @leave_type    [INT],    
               @approver      VARCHAR(50),    
               @status        VARCHAR(50),    
               @paid_leaves   FLOAT,    
               @unpaid_leaves FLOAT,    
               @half_day      INT  = 0,    
               @timesession   VARCHAR(10),    
               @remarks       VARCHAR(250),    
               @applyyear     INT)    
AS    
  BEGIN  
 DECLARE  @leavemodel  INT  
  
    DECLARE  @nextyear INT    
        
    DECLARE  @FirstTime INT    
        
    DECLARE  @trx_id INT    
        
    DECLARE  @from_date DATETIME    
        
    DECLARE  @to_date DATETIME    
        
    DECLARE  @leave_date DATETIME    
        
    DECLARE  @halfday_leave BIT    
        
    DECLARE  @unpaid_leave BIT    
        
    DECLARE  @company_code INT    
        
    DECLARE  @i float    
        
    DECLARE  @wdays_per_week FLOAT    
        
    DECLARE  @cnthalfday FLOAT    
        
    SET @nextyear = NULL    
        
    SET @cnthalfday = 0    
        
    IF (@applyyear != Year(Convert(DATETIME,@start_date,103)))    
      BEGIN    
        IF ((@applyyear - Year(Convert(DATETIME,@start_date,103))) <= 1)    
          BEGIN    
            SET @nextyear = @applyyear    
          END    
      END    
  
    SELECT @company_code = e.company_id,    
           @wdays_per_week = e.wdays_per_week, @leavemodel = c.Leave_Model    
    FROM   employee  e Inner Join Company c On e.Company_ID = c.Company_ID  
    WHERE  emp_code = @emp_code    
  
        
    INSERT INTO [emp_leaves]    
               ([emp_id],    
                [start_date],    
                [end_date],    
                [leave_type],    
                [approver],    
                [status],    
                [paid_leaves],    
                [unpaid_leaves],    
                [timesession],    
                [Remarks],  
    [Leave_Model])    
    VALUES     (@emp_code,    
                Convert(DATETIME,@start_date,103),    
                Convert(DATETIME,@end_date,103),    
                @leave_type,    
                @approver,    
                @status,    
                @paid_leaves,    
                @unpaid_leaves,    
                @timesession,    
                @remarks,  
    @leavemodel)    
        
    -- cater child table    
    SELECT @trx_id = @@IDENTITY    
    FROM   [emp_leaves]    
        
    SET @i = 1    
        
    SET @from_date = Convert(DATETIME,@start_date,103)    
        
    SET @to_date = Convert(DATETIME,@end_date,103)    
        
    SET @leave_date = @from_date    
        
    SET @halfday_leave = @half_day    
        
    SET @FirstTime = 0    
        
    WHILE @leave_date <= @to_date    
      BEGIN    
        SET @halfday_leave = 0    
            
        IF (@leave_date = @to_date)    
          BEGIN    
            IF (@half_day = 1)    
              BEGIN    
                SET @halfday_leave = 1    
              END    
          END    
            
        IF (@i - @cnthalfday) >= Round((@paid_leaves+@unpaid_leaves),0) AND @unpaid_leaves > 0    
          BEGIN    
            SET @unpaid_leave = 1    
          END    
        ELSE    
          BEGIN    
            SET @unpaid_leave = 0    
          END    
            
        IF dbo.Isholiday(@company_code,@leave_date) = 0    
          BEGIN    
            IF (@wdays_per_week = 5    
                AND Datename(dw,@leave_date) != 'Saturday'    
                AND Datename(dw,@leave_date) != 'Sunday')    
              BEGIN    
                IF (@unpaid_leave = 1    
                    AND @FirstTime = 0)    
                  BEGIN    
                    SET @FirstTime = 1    
                        
                    IF (@paid_leaves - Cast(@paid_leaves AS INT)) = 0.5  AND @half_day = 0    
                      BEGIN    
                        UPDATE emp_leaves_detail    
                        SET    halfday_leave = 1    
                        WHERE  trx_id = @trx_id    
                               AND leave_date = Dateadd(DAY,-1,@leave_date)    
                      END    
                 
                    IF (@unpaid_leaves - Cast(@unpaid_leaves AS INT)) = 0.5  AND @half_day = 0    
                      BEGIN    
                        INSERT INTO emp_leaves_detail    
                                   (trx_id,    
                                    leave_date,    
                                    halfday_leave,    
                                    unpaid_leave,    
                                    nextyear)    
                        VALUES     (@trx_id,    
                                    Dateadd(DAY,-1,@leave_date),    
                                    1,    
                                    1,    
                                    @nextyear)    
                      END    
                        
                    INSERT INTO emp_leaves_detail    
                               (trx_id,    
                                leave_date,    
                                halfday_leave,    
                                unpaid_leave,    
                                nextyear)    
                    VALUES     (@trx_id,    
                                @leave_date,    
                                @halfday_leave,    
                                @unpaid_leave,    
                                @nextyear)    
                  END    
                ELSE    
                  BEGIN    
                    INSERT INTO emp_leaves_detail    
                               (trx_id,    
                                leave_date,    
                                halfday_leave,    
                                unpaid_leave,    
                                nextyear)    
                    VALUES     (@trx_id,    
                                @leave_date,    
                                @halfday_leave,    
                                @unpaid_leave,    
                                @nextyear)    
                  END    
              END    
                
            IF (@wdays_per_week = 5.5    
                AND Datename(dw,@leave_date) = 'Saturday')    
              BEGIN    
                INSERT INTO emp_leaves_detail    
                           (trx_id,    
                            leave_date,    
                            halfday_leave,    
                            unpaid_leave,    
                            nextyear)    
                VALUES     (@trx_id,    
                            @leave_date,    
                            1,    
                            @unpaid_leave,    
                            @nextyear)    
              END    
                
            IF (@wdays_per_week = 5.5    
                AND Datename(dw,@leave_date) != 'Saturday'    
                AND Datename(dw,@leave_date) != 'Sunday')    
              BEGIN    
                IF (@unpaid_leave = 1    
                    AND @FirstTime = 0)    
                  BEGIN    
                    SET @FirstTime = 1    
                        
                    IF (@paid_leaves - Cast(@paid_leaves AS INT)) = 0.5 AND @half_day = 0    
                      BEGIN    
                        UPDATE emp_leaves_detail    
                        SET    halfday_leave = 1    
                        WHERE  trx_id = @trx_id    
                               AND leave_date = Dateadd(DAY,-1,@leave_date)    
                      END    
                        
                    IF (@unpaid_leaves - Cast(@unpaid_leaves AS INT)) = 0.5 AND @half_day = 0    
                      BEGIN    
                        INSERT INTO emp_leaves_detail    
               (trx_id,    
                                    leave_date,    
                                    halfday_leave,    
                                    unpaid_leave,    
                                    nextyear)    
                        VALUES     (@trx_id,    
                                    Dateadd(DAY,-1,@leave_date),    
                                    1,    
                                    1,    
                                    @nextyear)    
                      END    
                        
                    INSERT INTO emp_leaves_detail    
                    (trx_id,    
                                leave_date,    
                                halfday_leave,    
                                unpaid_leave,    
                                nextyear)    
                    VALUES     (@trx_id,    
                                @leave_date,    
                                @halfday_leave,    
                                @unpaid_leave,    
                                @nextyear)    
                  END    
                ELSE    
                  BEGIN    
                    INSERT INTO emp_leaves_detail    
                               (trx_id,    
                                leave_date,    
                                halfday_leave,    
                                unpaid_leave,    
                                nextyear)    
                    VALUES     (@trx_id,    
                                @leave_date,    
                                @halfday_leave,    
                                @unpaid_leave,    
                                @nextyear)    
                  END    
              END    
                
            IF ((@wdays_per_week = 6)    
                AND (Datename(dw,@leave_date) != 'Sunday'))    
              BEGIN    
                IF (@unpaid_leave = 1    
                    AND @FirstTime = 0)    
                  BEGIN    
                    SET @FirstTime = 1    
                        
                    IF (@paid_leaves - Cast(@paid_leaves AS INT)) = 0.5    
                       AND @half_day = 0    
                      BEGIN    
                        UPDATE emp_leaves_detail    
