								SELECT @LeaveAppliedForDays =  Datediff(dd,@FromDate,@Todate)+1
							END
						ELSE
							BEGIN
								SET @LeaveAppliedForDays = dbo.Fn_nowds(@Emp_Code, @FromDate, @Todate) - @ishalfday
							END

					--PRINT 'B'
                  END      
                END      
               ELSE      
                BEGIN
						IF @leave_type=10 
