                                                        WHEN (Pay_Frequency = 'H')
                                                        THEN Emp_Worked_Time_in_HrsMins - Emp_Total_OTWorked_in_HrsMins
                                                        ELSE 0
                                                   END ,
                        PubHol = CASE WHEN (T.PublicHoliday = 1) THEN 'Yes'
