                                      ELSE 'N/A'
                                 END ,
                        InOutEmpty = CASE WHEN (Emp_Total_OTWorked_in_Mins IS NULL AND
                                                @isInOutEmpty > 0
                                               ) THEN 0
                                          WHEN (Emp_Total_OTWorked_in_Mins < 0 AND
                                                @isInOutEmpty > 0
                                               ) THEN 1
                                          WHEN (Emp_Total_OTWorked_in_Mins > 0 AND
                                                @isInOutEmpty > 0
