        DECLARE @softdelete AS INT

        SET @ConsEarlyInBy = 0
        SET @startdate = CONVERT(DATETIME, @start_date, 103)
        SET @enddate = CONVERT(DATETIME, @end_date, 103)

		 SELECT *                
