           AS (SELECT Row_number() OVER (PARTITION BY emp_id ORDER BY 
                      createddate+ 
                      fromdate 
                      DESC) AS 
                      'RowNo', 
                      id, 
                      emp_id, 
                      payrate 
               FROM   employeepayhistory 
               WHERE  fromdate <= @enddtsub) 
      SELECT emp_id, 
             payratecust=CASE 
                           WHEN Len(CONVERT(VARCHAR(10), Decryptbyasymkey( 
                                    Asymkey_id ( 
                                    'AsymKey'), 
                                    payrate))) > 
                                0 THEN CONVERT(DECIMAL(18, 2), CONVERT( VARCHAR( 
                                       10 
                                       ), 
                                       Decryptbyasymkey( 
                                       Asymkey_id( 
                                       'AsymKey'), payrate))) 
