               @enddtsub AND e.joining_date > @startdtsub AND e.pr_date >= 
               e.joining_date AND 
               dt.dateinyear >= e.pr_date AND dt.dateinyear <= 
               e.termination_date 
               AND 
               ( 
               Datepart 
               (weekday, dateinyear) = 7) ) OR (e.pr_date IS NOT NULL AND 
               e.termination_date 
               IS 
               NOT NULL AND e.termination_date >= @startdtsub AND 
               e.termination_date 
               <= 
