                       AND dt.dateinyear >= e.joining_date 
                       AND dt.dateinyear <= e.termination_date 
                       AND ( Datepart(weekday, dateinyear) = 1 ) ) 
                      OR ( ( ( e.termination_date >= @startdtsub 
