               ((e.termination_date IS NULL AND ( 
               e.joining_date > @startdtsub AND e.joining_date <= @enddtsub ) ) 
               AND 
               dt.dateinyear >= e.joining_date AND dt.dateinyear <= @enddtsub 
               AND 
               ( 
               Datepart( 
               weekday, 
