      --MONTH(trx_period) = @startmonth AND                                                          
      ( trx_period >= @startdtsub 
        AND trx_period <= @enddtsub ) 
      AND YEAR(trx_period) = @startyear ) 
      AND AT.cpf = 'No' 
      AND ( AT.type_of_wage = 'O' 
             OR AT.type_of_wage IS NULL ) 
      AND ( ea.claimstatus = 'Approved' 
