      --Calc Employee Overtime                                 
      SELECT emp_code, 
             dbo.Converttohours(SUM(dbo.Converttominutes(overtime1))) overtime1, 
             dbo.Converttohours(SUM(dbo.Converttominutes(overtime2))) overtime2, 
             SUM(days_work)                                           days_work, 
             dbo.Converttohours(SUM(dbo.Converttominutes(nh_work)))   nh_work 
      INTO   #eot 
      FROM   emp_overtime 
      WHERE  ( ( trx_month = @monthidintbl 
                 AND 
                 ----shahsank changes for client TCC                                                       
                 ( payrollstdate >= @startdtsub 
                   AND payrollstdate <= @enddtsub ) 
                 AND trx_year = @startyear ) 
                OR trx_month IS NULL ) 
      GROUP  BY emp_code 

      --Select @TotalDaysCount_5,@TotalDaysCount_5p,@TotalDaysCount_6,@TotalDaysCount_7                                                                                                              
      SELECT *, 
             ( [Basic] - totalunpaid ) actualbasic, 
             cpfordinary = CASE 
                             WHEN ( cpf = 'Y' 
                                    AND emp_type = 'SPR' 
                                     OR emp_type = 'SDPR' 
                                     OR emp_type = 'SC' ) 
                                  AND [BasicPR] <= 0 THEN ( 
                             ( [Basic] - totalunpaid ) + daysworkedrate + 
                             ot1 + ot2 + add4ow ) - ( 
                                                          dedcpfamt ) 
                             ELSE ( ( [BasicPR] - unpaiddeductpr ) + 
                                    daysworkedrate + 
                                    ot1 + 
                                    ot2 + add4ow ) - ( 
                                  dedcpfamt ) 
                           END, 
             grosswithoutaddition = CASE 
                                      WHEN ( cpf = 'Y' 
                                             AND emp_type = 'SPR' 
                                              OR emp_type = 'SDPR' 
                                              OR emp_type = 'SC' ) 
                                           AND [BasicPR] <= 0 
                                           AND @roundnetpay >= 0 THEN Isnull( 
                                      CAST( 
                                      Round( 
                                      ( ( 
                                        [Basic] - totalunpaid ) 
                                                + ot1 + ot2 + 
                                                daysworkedrate 
                                      ), 
                                      @roundnetpay 
                                      ) 
                                                                      AS 
                                      DECIMAL(18, 2)), 0) 
                                      WHEN ( cpf = 'Y' 
                                             AND emp_type = 'SPR' 
                                              OR emp_type = 'SDPR' 
                                              OR emp_type = 'SC' ) 
                                           AND [BasicPR] <= 0 
                                           AND @roundnetpay = -1 THEN 
                                      Isnull(CAST(( 
                                      ( [Basic] - totalunpaid ) + ot1 
                                      + ot2 + daysworkedrate ) AS 
                                                  DECIMAL(18, 2)), 0) 
                                      WHEN ( [BasicPR] > 0 
                                             AND @roundnetpay = -1 ) THEN 
                                      Isnull(CAST(( ( [BasicPR] - unpaiddeductpr 
                                                    ) + ot1 + ot2 + 
                                                    daysworkedrate ) AS 
                                                  DECIMAL(18, 2)), 0) 
                                      WHEN ( [BasicPR] > 0 
                                             AND @roundnetpay >= 0 ) THEN Isnull 
                                      ( 
                                      CAST(Round(( ( [BasicPR] 
                                                     - unpaiddeductpr ) + ot1 + 
                                                   ot2 + 
                                                   daysworkedrate 
                                                 ), 
                                                       @roundnetpay) AS 
                                           DECIMAL( 
                                                  18, 2)), 0) 
                                      WHEN ( emp_type = 'WP' 
                                              OR emp_type = 'DP' 
                                              OR emp_type = 'EP' 
                                              OR emp_type = 'SP' ) 
                                           AND @roundnetpay >= 0 THEN Isnull( 
                                      CAST( 
                                      Round( 
                                      ( ( 
                                        [Basic] - totalunpaid ) 
                                                + ot1 + ot2 + 
                                                daysworkedrate 
                                      ), 
                                      @roundnetpay 
                                      ) 
                                                                      AS 
                                      DECIMAL(18, 2)), 0) 
                                      WHEN ( emp_type = 'WP' 
                                              OR emp_type = 'DP' 
                                              OR emp_type = 'EP' 
                                              OR emp_type = 'SP' ) 
                                           AND @roundnetpay >= 0 THEN 
                                      Isnull(CAST(( 
                                      ( [Basic] - totalunpaid ) + ot1 
                                      + ot2 + daysworkedrate ) AS 
                                                  DECIMAL(18, 2)), 0) 
                                    END 
      INTO   #priorgenpayroll 
      FROM   (SELECT *, 
                     [BasicPR] = CASE 
                                   WHEN ( pr_date >= @startdtsub ) 
                                        AND ( emp_type = 'SPR' 
                                               OR emp_type = 'SDPR' 
                                               OR emp_type = 'SC' ) THEN 
                                   Round( 
                                   CONVERT(DECIMAL(18, 2), (actprwrkgdaysspan * 
                                   basicdayrateworound)) 
                                   , 
                                   2) 
                                   ELSE -1 
                                 --Else Round(convert(decimal(8,2),(ActWrkgDaysSpan*BasicDayRateWORound)),2)                                                                                                                         
                                 END, 
                     [Basic] = CASE 
                                 WHEN paytype = 'D' 
                                      AND CONVERT(DECIMAL(18, 2), payratecust) 
                                          <= 
                                          0 
                               THEN 0 
                                 --WHEN ActPRWrkgDaysSpan > 0 THEN Round(CONVERT(DECIMAL(18, 2), (ActWrkgDaysSpan * BasicDayRateWORound)), 2)      
                                 WHEN actprwrkgdaysspan > 0 THEN Round( 
                                 CONVERT(DECIMAL(18, 2), (actwrkgdaysspan 
                                 * basicdayrateworound)), 2) 
                                 ELSE Round(CONVERT(DECIMAL(18, 2), ( 
                                            actwrkgdaysspan * 
                                            basicdayrateworound)), 2) 
                               END 
              FROM   (SELECT e.emp_code, 
                             e.fullname, 
                             e.emp_type, 
                             e.deptname, 
                             e.groupname, 
                             e.employeegiro, 
                             e.employergiro, 
                             e.girobank, 
                             e.prage, 
                             e.workingdaysinweek, 
                             e.ot, 
                             e.cpf, 
                             e.cpftype, 
                             e.paytype, 
                             e.daily_rate_mode, 
                             e.daily_rate, 
                             e.pay_mode, 
                             e.hourly_rate, 
                             e.join_date                            join_date, 
                             e.term_date                            term_date, 
                             e.pr_date                              pr_date, 
                             e.rolldays, 
                             e.wrkgdaysinroll, 
                             e.basicpayinroll, 
                             e.subpaydays, 
                             e.wrkgdaysinsubpay, 
                             e.basicdayrate, 
                             e.basicdayrateworound, 
                             ( e.basicdayrate * e.actwrkgdaysspan ) 
                             basicnorsubpay 
                             , 
                             e.unpaidfullday, 
                             e.unpaidhalfday, 
                             e.paidfullday, 
                             e.paidhalfday, 
                             e.unpaiddeduct, 
                             e.unpaidfulldaypr, 
                             e.unpaidhalfdaypr, 
                             e.unpaiddeductpr, 
                             ( e.unpaiddeduct + e.unpaiddeductpr )  totalunpaid, 
                             e.days_work, 
                             e.nh_work, 
                             e.ot1rate, 
                             e.nhhrs, 
                             e.ot1hrs, 
                             e.ot1, 
                             e.ot2rate, 
                             e.ot2hrs, 
                             e.ot2, 
                             e.add4nw, 
                             e.add4ow, 
                             e.nh, 
                             e.add4aw, 
                             e.add4awnocpf, 
                             e.additionalwages, 
                             e.dedamt, 
                             e.dedcpfamt, 
                             e.actsatdayspan, 
                             e.actsundayspan, 
                             e.actwrkgdaysspan, 
                             e.actprsatdayspan, 
                             e.actprsundayspan, 
                             actprwrkgdaysspan = CASE 
                                                   WHEN ( e.pr_date IS NULL 
                                                           OR e.pr_date > 
                                                              @enddtsub ) 
                                                 THEN 0 
                                                   WHEN ( e.pr_date IS NOT NULL 
                                                          AND e.term_date IS NOT 
                                                              NULL 
                                                          AND ( e.pr_date >= 
                                                                @startdtsub 
                                                                AND e.pr_date <= 
                                                                    @enddtsub ) 
                                                          AND e.term_date >= 
                                                              @enddtsub 
                                                        ) THEN 
                                                   ( 
                                                   Datediff(dw, e.pr_date, 
                                                   @enddtsub) 
                                                   + 1 ) - 
                                                   ( 
                             actprsatdayspan + actprsundayspan 
                                                   ) 
                                                   WHEN ( e.pr_date IS NOT NULL 
                                                          AND e.term_date IS NOT 
                                                              NULL 
                                                          AND ( e.pr_date >= 
                                                                @startdtsub 
                                                                AND e.pr_date <= 
                                                                    @enddtsub ) 
                                                          AND e.term_date < 
                                                              @enddtsub 
                                                        ) THEN 
                                                   ( 
                                                   Datediff(dw, e.pr_date, 
                                                   e.term_date) + 1 ) 
                                                   - ( 
                             actprsatdayspan + actprsundayspan ) 
                             WHEN ( e.pr_date IS NOT NULL 
                                    AND e.term_date IS NULL 
                                    AND e.pr_date <= @startdtsub 
                                    AND e.join_date <= @enddtsub 
                                  ) THEN 
                             e.actwrkgdaysspan 
                             WHEN ( e.pr_date IS NOT NULL 
                                    AND e.term_date IS NOT NULL 
                                    AND e.pr_date < @startdtsub 
                                    AND e.join_date < 
                                        @startdtsub 
                                    AND e.term_date <= @enddtsub 
                                  ) THEN 
                             ( 
                             Datediff(dw, @startdtsub, 
                             e.term_date) + 1 
                             ) - ( 
                             actprsatdayspan + actprsundayspan ) 
                             WHEN ( e.pr_date IS NOT NULL 
                                    AND e.term_date IS NOT NULL 
                                    AND e.pr_date < @startdtsub 
                                    AND e.join_date >= 
                                        @startdtsub 
                                    AND e.join_date <= @enddtsub 
                                    AND e.term_date <= @enddtsub 
                                  ) THEN 
                             e.actwrkgdaysspan 
                             WHEN ( e.pr_date IS NOT NULL 
                                    AND e.term_date IS NULL 
                                    AND ( e.pr_date >= 
                                          @startdtsub 
                                          AND e.pr_date <= 
                                              @enddtsub ) 
                                    AND e.join_date <= 
                                        @startdtsub ) 
                                                 THEN ( 
                                                   Datediff(dw, e.pr_date, 
                                                   @enddtsub) 
                                                   + 1 ) 
                                                      - 
                                                          ( 
                             actprsatdayspan + actprsundayspan ) 
                             WHEN ( e.pr_date IS NOT NULL 
                                    AND e.term_date IS NULL 
                                    AND ( e.pr_date >= 
                                          @startdtsub 
                                          AND e.pr_date <= 
                                              @enddtsub ) 
                                    AND e.join_date >= 
                                        @startdtsub 
                                    AND e.join_date <= @enddtsub 
                                  ) 
                                  AND e.join_date < e.pr_date 
                                                 THEN ( 
                                                   Datediff(dw, e.pr_date, 
                                                   @enddtsub) 
                                                   + 1 
                                                      ) - 
                                                      ( 
                             actprsatdayspan + actprsundayspan ) 
                             WHEN ( e.pr_date IS NOT NULL 
                                    AND e.term_date IS NULL 
                                    AND ( e.pr_date >= 
                                          @startdtsub 
                                          AND e.pr_date <= 
                                              @enddtsub ) 
                                    AND e.join_date >= 
                                        @startdtsub 
                                    AND e.join_date <= @enddtsub 
                                  ) 
                                  AND e.join_date >= e.pr_date 
                                                 THEN ( 
                                                   Datediff(dw, e.join_date, 
                                                   @enddtsub 
                                                   ) + 1 ) 
                                                   - ( 
                             actprsatdayspan + actprsundayspan ) 
                             ELSE e.actwrkgdaysspan 
                                                 END, 
                             e.daysworkedrate, 
                             e.calculatecpf, 
                             e.payratecust 
                      FROM   (SELECT t.emp_code, 
                                     t.fullname, 
                                     t.emp_type, 
                                     t.deptname, 
                                     t.groupname, 
                                     t.employeegiro, 
                                     t.employergiro, 
                                     t.girobank, 
                                     t.prage, 
                                     t.workingdaysinweek, 
                                     t.ot, 
                                     t.cpf, 
                                     t.cpftype, 
                                     t.paytype, 
                                     t.daily_rate_mode, 
                                     t.daily_rate, 
                                     t.pay_mode, 
                                     t.hourly_rate, 
                                     t.join_date         join_date, 
                                     t.term_date         term_date, 
                                     t.pr_date           pr_date, 
                                     t.rolldays, 
                                     t.wrkgdaysinroll, 
