                                     t.unpaidfullday, 
                                     t.unpaidhalfday, 
                                     t.paidfullday, 
                                     t.paidhalfday, 
                                     t.unpaidfulldaypr, 
                                     t.unpaidhalfdaypr, 
                                     t.days_work, 
                                     t.nh_work, 
                                     t.ot1rate, 
                                     Isnull(t.nhhrs, 0)  nhhrs, 
