                                     ot1 = CASE 
                                             WHEN @roundaddition = -1 THEN t.ot1 
                                             ELSE Round(t.ot1, @roundaddition) 
                                           END, 
                                     nh = CASE 
                                            WHEN @roundaddition = -1 THEN t.nh 
                                            ELSE Round(t.nh, @roundaddition) 
                                          END, 
                                     t.ot2rate, 
                                     Isnull(t.ot2hrs, 0) ot2hrs, 
                                     ot2 = CASE 
                                             WHEN @roundaddition = -1 THEN t.ot2 
                                             ELSE Round(t.ot2, @roundaddition) 
                                           END, 
                                     add4nw = CASE 
                                                WHEN @roundaddition = -1 THEN 
                                                t.add4nw 
                                                ELSE Round(t.add4nw, 
                                                     @roundaddition) 
                                              END, 
                                     add4ow = CASE 
                                                WHEN @roundaddition = -1 THEN 
                                                t.add4ow 
                                                ELSE Round(t.add4ow, 
                                                     @roundaddition) 
                                              END, 
                                     add4aw = CASE 
                                                WHEN @roundaddition = -1 THEN 
                                                t.add4aw 
                                                ELSE Round(t.add4aw, 
                                                     @roundaddition) 
                                              END, 
                                     add4awnocpf = CASE 
                                                     WHEN @roundaddition = -1 
                                                   THEN 
                                                     t.add4awnocpf 
                                                     ELSE Round(t.add4awnocpf, 
                                                          @roundaddition 
                                                          ) 
                                                   END, 
                                     additionalwages = 
                                     CASE 
                                       WHEN @roundaddition = -1 
                                     THEN 
                                       t.additionalwages 
                                       ELSE Round(t.additionalwages, 
                                            @roundaddition 
                                            ) 
                                     END, 
                                     dedamt = CASE 
                                                WHEN @rounddeduction = -1 THEN 
                                                t.dedamt 
                                                ELSE Round(t.dedamt, 
                                                     @rounddeduction) 
                                              END, 
                                     dedcpfamt = CASE 
                                                   WHEN @rounddeduction = -1 
                                                 THEN 
                                                   t.dedcpfamt 
                                                   ELSE Round(t.dedcpfamt, 
                                                        @rounddeduction) 
                                                 END, 
                                     t.actsatdayspan, 
                                     t.actsundayspan, 
                                     actwrkgdaysspan = 
                                     CASE 
                                       WHEN ( 
                                     t.join_date IS NULL ) 
                                                       THEN 
                                                         t.wrkgdaysinsubpay 
                                                         WHEN ( 
                                     t.join_date <= @startdtsub 
                                     AND t.term_date IS NULL ) 
                                                              AND halfsalary = 0 
                                                       THEN 
                                                         ( 
                                                         t.wrkgdaysinsubpay ) 
                                                         WHEN ( 
                                     t.join_date <= @startdtsub 
                                     AND t.term_date IS NULL ) 
                                                              AND halfsalary = 1 
                                                       THEN 
                                                         t.wrkgdaysinroll / 2 
                                                         --(T.WrkgDaysInSubPay)       
                                                         WHEN ( 
                                     t.join_date <= @startdtsub 
                                     AND t.term_date IS NULL ) 
                                                              AND halfsalary = 2 
                                                              AND 
                                     @endatesubmonth 
                                     = 15 
                                                       THEN 0 
                                                         --t.wrkgdaysinroll/2    
                                                         WHEN ( 
                                     t.join_date <= @startdtsub 
                                     AND t.term_date IS NULL ) 
                                                              AND halfsalary = 2 
                                                              AND 
                                     @endatesubmonth 
                                     != 
                                     15 THEN 
                                                         t.wrkgdaysinroll 
                                                         WHEN ( 
                                     t.join_date > @startdtsub 
                                     AND t.term_date IS NULL ) 
                                                       THEN ( ( 
                                     @RollDays - DAY(t.join_date) 
                                     - 
                                     Datediff(dw, @enddtsub, 
                                     @enddtproll) 
                                          ) + 1 ) 
                                            - ( actsatdayspan + 
                                                actsundayspan ) 
                                     WHEN ( 
                                     t.join_date >= @startdtsub 
                                     AND t.term_date IS NOT NULL 
                                     AND t.term_date > @enddtsub ) 
                                                       THEN ( 
                                     Datediff(dw, t.join_date, 
                                     @enddtsub) 
                                     + 1 ) 
                                     - ( 
                                     actsatdayspan + actsundayspan 
                                       ) 
                                     WHEN ( 
                                     t.join_date <= @startdtsub 
                                     AND t.term_date IS NOT NULL 
                                     AND t.term_date <= @enddtsub 
                                                              ) THEN 
                     ( 
                     Datediff(dw, @startdtsub, 
                     t.term_date) + 1 ) 
                     - 
                                                                ( 
                     actsatdayspan + actsundayspan ) 
                     WHEN ( t.join_date >= @startdtsub 
                            AND t.join_date <= @enddtsub 
                            AND t.term_date IS NOT NULL 
                            AND t.term_date <= @enddtsub 
                          ) THEN 
                     ( 
