                             CROSS JOIN (SELECT * 
                                         FROM   dateinyear 
                                         WHERE  ( dateinyear BETWEEN 
                                                  CONVERT(DATETIME, @start_date, 
                                                  103) AND 
                                                  CONVERT(DATETIME, @end_date, 
                                                  103 
                                                  ) )) f 
                      WHERE  f.dateinyear BETWEEN e.start AND e.enddate) f 
                     INNER JOIN subproject s 
