            SELECT subprojectid, 
                   entrydate, 
                   emp_id 
            FROM   multiprojectassigned 
        END 
      ELSE 
        BEGIN 
            INSERT INTO #multiprojectassigned 
            SELECT subprojectid, 
                   entrydate, 
                   emp_id 
            FROM   multiprojectassignedeoy 
        END 

      SELECT * 
      INTO   #leavetype 
      FROM   leave_types lt 
      WHERE  id IN (SELECT DISTINCT lt.id id 
                    FROM   emp_leaves_detail ed 
