       DECLARE @cnt INT  
       DECLARE @NextString NVARCHAR(40)  
  DECLARE @Pos INT  
  DECLARE @NextPos INT  
  DECLARE @String NVARCHAR(40)  
  DECLARE @Delimiter NVARCHAR(40)  
  DECLARE @sub1 NVARCHAR(40)  
